import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

type EventData = {
  id: number;
  name: string;
  description: string;
  type: string;
};

const EventListPage = () => {
  const [events, setEvents] = useState<EventData[]>([]);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:3001/events');
        setEvents(response.data);
      } catch (error) {
        console.error('Error al cargar los eventos:', error);
      }
    };

    fetchEvents();
  }, []);

  const handleUpdate = (eventId: number) => {
    // Implementa la lógica de actualización del evento aquí y redirige al usuario a la página de actualización
  };

  const handleDelete = (eventId: number) => {
    // Implementa la lógica de eliminación del evento aquí y redirige al usuario a la página de eliminación
  };

  return (
    <div>
      <h1>Event List</h1>
      <Link to="/create">Create Event</Link>
      <ul>
        {events.map((event) => (
          <li key={event.id}>
            <p>ID: {event.id}</p>
            <p>Name: {event.name}</p>
            <p>Description: {event.description}</p>
            <p>Type: {event.type}</p>
            <button onClick={() => handleUpdate(event.id)}>Update</button>
            <button onClick={() => handleDelete(event.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EventListPage;
