import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

type EventData = {
  id: number;
  name: string;
  description: string;
  type: string;
  priority: number; // Agregamos la propiedad 'priority'
};

const EventListPage = () => {
  const [events, setEvents] = useState<EventData[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:3001/events');
        setEvents(response.data);
      } catch (error) {
        console.error('Error al cargar los eventos:', error);
      }
    };

    fetchEvents();
  }, []);

  const handleUpdate = (eventId: number) => {
    // Redirige al usuario a la página de actualización con el ID del evento
    navigate(`/update/${eventId}`);
  };

  const handleDelete = async (eventId: number) => {
    try {
      // Realiza la lógica para eliminar el evento utilizando su ID
      await axios.delete(`http://localhost:3001/events/${eventId}`);
      // Actualiza la lista de eventos después de eliminar
      const updatedEvents = events.filter((event) => event.id !== eventId);
      setEvents(updatedEvents);
    } catch (error) {
      console.error('Error al eliminar el evento:', error);
    }
  };

  return (
    <div>
      <h1>Event List</h1>
      <Link to="/create">Create Event</Link>
      <ul>
        {events.map((event) => (
          <li key={event.id}>
            <p>ID: {event.id}</p>
            <p>Name: {event.name}</p>
            <p>Description: {event.description}</p>
            <p>Type: {event.type}</p>
            <p>Priority: {event.priority}</p> {/* Muestra la prioridad */}
            <button onClick={() => handleUpdate(event.id)}>Update</button>
            <button onClick={() => handleDelete(event.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EventListPage;
