import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UpdateEvent = () => {
  const navigate = useNavigate();

  const [eventData, setEventData] = useState({
    name: '',
    description: '',
    type: '',
    priority: 0,
  });

  const [events, setEvents] = useState([]); // Lista de eventos desde el backend
  const [selectedEvent, setSelectedEvent] = useState<number | null>(null);

  useEffect(() => {
    // Cargar la lista de eventos desde el backend
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:3001/events');
        setEvents(response.data);
      } catch (error) {
        console.error('Error al cargar los eventos:', error);
      }
    };

    fetchEvents();
  }, []);

  const handleUpdate = async () => {
    if (selectedEvent === null) {
      return; // No se ha seleccionado ningún evento
    }

    try {
      const response = await axios.put(
        `http://localhost:3001/events/${selectedEvent}`,
        eventData
      );
      console.log('Evento actualizado:', response.data);

      // Redirigir al usuario a la vista del evento actualizado o a otra ruta
      navigate(`/view/${selectedEvent}`);
    } catch (error) {
      console.error('Error al actualizar evento:', error);
    }
  };

  return (
    <div>
      <h2>Update Event</h2>
      <form>
        <select onChange={(e) => setSelectedEvent(parseInt(e.target.value, 10))}>
          <option value="">Selecciona un evento</option>
          {events.map((event: any) => (
            <option key={event.id} value={event.id}>
              {event.name}
            </option>
          ))}
        </select>
        <br />
        {selectedEvent !== null && (
          <>
            <input
              type="text"
              placeholder="Name"
              value={eventData.name}
              onChange={(e) => setEventData({ ...eventData, name: e.target.value })}
            />
            <input
              type="text"
              placeholder="Description"
              value={eventData.description}
              onChange={(e) => setEventData({ ...eventData, description: e.target.value })}
            />
            <input
              type="text"
              placeholder="Type"
              value={eventData.type}
              onChange={(e) => setEventData({ ...eventData, type: e.target.value })}
            />
            <input
              type="number"
              placeholder="Priority"
              value={eventData.priority}
              onChange={(e) => setEventData({ ...eventData, priority: parseInt(e.target.value, 10) })}
            />
            <br />
            <button type="button" onClick={handleUpdate}>
              Update event
            </button>
          </>
        )}
      </form>
    </div>
  );
};

export default UpdateEvent;
