import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

// Define tipos para selectedEventId y eventData
type EventData = {
  name: string;
  description: string;
  type: string;
  priority: number;
};

const UpdateEvent = () => {
  const navigate = useNavigate();

  const [eventData, setEventData] = useState<EventData>({
    name: '',
    description: '',
    type: '',
    priority: 0,
  });

  const [events, setEvents] = useState<any[]>([]);
  const [selectedEventId, setSelectedEventId] = useState<string>('');

  const handleUpdate = async () => {
    if (!selectedEventId) {
      return;
    }

    try {
      const response = await axios.put(
        `http://localhost:3001/events/${selectedEventId}`,
        eventData
      );
      console.log('Evento actualizado:', response.data);

      // Redirigir al usuario a la vista del evento actualizado
      navigate(`/view/${selectedEventId}`);
    } catch (error) {
      console.error('Error al actualizar evento:', error);
    }
  };

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:3001/events');
        setEvents(response.data);
      } catch (error) {
        console.error('Error al cargar los eventos:', error);
      }
    };

    fetchEvents();
  }, []);

  return (
    <div>
      <h2>Update Event</h2>
      <form>
        <select onChange={(e) => setSelectedEventId(e.target.value)}>
          <option value="">Selecciona un evento</option>
          {events.map((event: any) => (
            <option key={event.id} value={event.id}>
              {event.name}
            </option>
          ))}
        </select>
        <br />
        {selectedEventId && (
          <>
            <input
              type="text"
              placeholder="Name"
              value={eventData.name}
              onChange={(e) => setEventData({ ...eventData, name: e.target.value })}
            />
            <input
              type="text"
              placeholder="Description"
              value={eventData.description}
              onChange={(e) =>
                setEventData({ ...eventData, description: e.target.value })
              }
            />
            <input
              type="text"
              placeholder="Type"
              value={eventData.type}
              onChange={(e) => setEventData({ ...eventData, type: e.target.value })}
            />
            <input
              type="number"
              placeholder="Priority"
              value={eventData.priority}
              onChange={(e) =>
                setEventData({ ...eventData, priority: parseInt(e.target.value) })
              }
            />
            <br />
            <button type="button" onClick={handleUpdate}>
              Update event
            </button>
          </>
        )}
      </form>
    </div>
  );
};

export default UpdateEvent;
