import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UpdateEvent = () => {
  const navigate = useNavigate();

  const [eventData, setEventData] = useState({
    name: '',
    description: '',
    type: '',
    priority: 0,
  });

  const selectedEventId = 1; // ID del evento seleccionado, reemplázalo por el ID correcto

  const handleUpdate = async () => {
    try {
      const response = await axios.put(
        `http://localhost:3001/events/${selectedEventId}`,
        eventData
      );
      console.log('Evento actualizado:', response.data);

      // Redirigir al usuario a la vista del evento actualizado con los nuevos datos en la URL
      navigate(
        `/view/${selectedEventId}?name=${eventData.name}&description=${eventData.description}&type=${eventData.type}&priority=${eventData.priority}`
      );
    } catch (error) {
      console.error('Error al actualizar evento:', error);
    }
  };

  return (
    <div>
      <h2>Update Event</h2>
      <form>
        <input
          type="text"
          placeholder="Name"
          value={eventData.name}
          onChange={(e) => setEventData({ ...eventData, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          value={eventData.description}
          onChange={(e) =>
            setEventData({ ...eventData, description: e.target.value })
          }
        />
        <input
          type="text"
          placeholder="Type"
          value={eventData.type}
          onChange={(e) => setEventData({ ...eventData, type: e.target.value })}
        />
        <input
          type="number"
          placeholder="Priority"
          value={eventData.priority}
          onChange={(e) =>
            setEventData({ ...eventData, priority: e.target.value })
          }
        />
        <br />
        <button type="button" onClick={handleUpdate}>
          Update event
        </button>
      </form>
    </div>
  );
};

export default UpdateEvent;
