import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

type EventData = {
  id: number;
  name: string;
  description: string;
  type: string;
  priority: number;
};

const EventListPage = () => {
  const [events, setEvents] = useState<EventData[]>([]);
  const [editEventId, setEditEventId] = useState<number | null>(null); // Nuevo estado para rastrear el evento en edición
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:3001/events');
        setEvents(response.data);
      } catch (error) {
        console.error('Error al cargar los eventos:', error);
      }
    };

    fetchEvents();
  }, []);

  const handleUpdate = (eventId: number) => {
    setEditEventId(eventId); // Establece el ID del evento que se está editando
  };

  const handleCancelEdit = () => {
    setEditEventId(null); // Cancela la edición
  };

  const handleSaveUpdate = async (eventId: number) => {
    // Implementa la lógica para guardar la actualización usando el evento y el evento ID
    // ...

    // Limpia el estado de edición
    setEditEventId(null);
  };

  const handleDelete = async (eventId: number) => {
    try {
      // Realiza la lógica para eliminar el evento utilizando su ID
      await axios.delete(`http://localhost:3001/events/${eventId}`);
      // Actualiza la lista de eventos después de eliminar
      const updatedEvents = events.filter((event) => event.id !== eventId);
      setEvents(updatedEvents);
    } catch (error) {
      console.error('Error al eliminar el evento:', error);
    }
  };

  const handleEditInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>,
    eventId: number
  ) => {
    // Implementa la lógica para manejar los cambios en los campos de edición
    // ...
  };

  return (
    <div>
      <h1>Event List</h1>
      <Link to="/create">Create Event</Link>
      <ul>
        {events.map((event) => (
          <li key={event.id}>
            <p>ID: {event.id}</p>
            {editEventId === event.id ? (
              <div>
                <input
                  type="text"
                  name="name"
                  value={event.name}
                  onChange={(e) => handleEditInputChange(e, event.id)}
                />
                {/* Agrega campos de entrada para otros atributos que deseas actualizar */}
                <button onClick={() => handleSaveUpdate(event.id)}>Save</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </div>
            ) : (
              <div>
                <p>Name: {event.name}</p>
                <p>Description: {event.description}</p>
                <p>Type: {event.type}</p>
                <p>Priority: {event.priority}</p>
                <button onClick={() => handleUpdate(event.id)}>Edit</button>
                <button onClick={() => handleDelete(event.id)}>Delete</button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EventListPage;
