// ViewEvent.tsx
import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

type EventData = {
  id: number;
  name: string;
  description: string;
  type: string;
};

const ViewEvent = ({ updatedEvent }: { updatedEvent: EventData | null }) => {
  const [events, setEvents] = useState<EventData[]>([]);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:3001/events');
        setEvents(response.data);
      } catch (error) {
        console.error('Error al cargar los eventos:', error);
      }
    };

    fetchEvents();
  }, []);

  const updateEventInList = useCallback(
    (eventId: string, updatedEvent: EventData) => {
      // Actualiza la lista de eventos con el evento actualizado
      const updatedEvents = events.map((event) => {
        if (event.id === parseInt(eventId)) {
          return updatedEvent;
        }
        return event;
      });
      setEvents(updatedEvents);
    },
    [events]
  );

  useEffect(() => {
    // Si hay un evento actualizado, actualízalo en la lista de eventos
    if (updatedEvent) {
      updateEventInList(updatedEvent.id.toString(), updatedEvent);
    }
  }, [updatedEvent, updateEventInList]);

  return (
    <div>
      <h2>Event List</h2>
      {events.length > 0 ? (
        <ul>
          {events.map((event, index) => (
            <li key={event.id}>
              <p>ID: {event.id}</p>
              <p>Name: {event.name}</p>
              <p>Description: {event.description}</p>
              <p>Type: {event.type}</p>
              <Link to={`/update/${event.id}`}>Update</Link>
            </li>
          ))}
        </ul>
      ) : (
        <p>No events available.</p>
      )}
    </div>
  );
};

export default ViewEvent;
